        self.Ui.setupUi(self.ventanaLogin)
        self.ventanaLogin.setWindowTitle("ULAGOS Market")
        self.ventanaLogin.setWindowIcon(QtGui.QIcon("icono/martin.png"))
        self.ventanaLogin.show()

        self.Registrar.close()
    

